INSERT INTO `recetas` (`id`,`nombre`,`ingredientes`,`instrucciones`) VALUES (1,'Lentejas','Lentejas, patata, zanahoria, chorizo, panceta, tomate seco, cebolla, pimiento, ajo, tomate, pan, AOVE, sal, pimenton.','Realizar el sofrito con las verduras y el pan frito. Cocer las lentejas con el chorizo, panceta, patata, zanahoria y tomate seco durante 10 min. Pasar la batidora al sofrito y al pan. Añadir a la cocción y dejar 5 min más.');
INSERT INTO `recetas` (`id`,`nombre`,`ingredientes`,`instrucciones`) VALUES (2,'Pure de verdura','Patata, zanahoria, calabacín, cebolla, pimiento rojo, tomates, puerro, acelgas, apio, sal, pimienta.','Cocer todas las verduras con agua durante 15 min. Pasar la batidora.');
INSERT INTO `recetas` (`id`,`nombre`,`ingredientes`,`instrucciones`) VALUES (4,'Tortilla patata','patata, huevo, leche, sal, AOVE','Freir la patata, escurrir el aceite y enfriar. Una vez fría, añadir huevo batido y volver a cocinar en la sarten, al punto deseado ');
INSERT INTO `recetas` (`id`,`nombre`,`ingredientes`,`instrucciones`) VALUES (5,'Macarrones','Pasta, carne picada, chorizo, tomate, zanahoria sal, AOVE, pimienta, tabasco.','Cocer la pasta. Freir carne picada, zanahoria y chorizo. Añadir el tomate con una gota de tabasco. Mezclar sofrito con los macarrones');
